import React, { Component } from 'react';
import { white, black } from 'ansi-colors';

class Card extends Component {
    constructor(props) {
        this.state = {timer: props.time}
    }

    style = {
        backgroundColor: "white",
        border: "2px solid black",
        borderRadius: "4px",
        width: "20%",
        boxShadow: "1px 1px #545454",
        padding: "10px",
    }

    clickHandler = () => {
        alert("works");
    }

    render() {
        let content = this.props.content;
        return (
            <div style = {this.style} onClick={() => this.clickHandler()}>
                Case Name: {this.props.caseName} <br/>
                Assignee: {this.props.assignee} <br/>
                Estimate: {this.props.estimate} <br/>   
            </div>
        );
    }
}

export default Card;
