import React, { Component } from 'react';
import { white, black } from 'ansi-colors';

class Card extends Component {

    style = {
        backgroundColor: "white",
        border: "2px solid black",
        borderRadius: "4px",
        width: "20%",
        boxShadow: "1px 1px #545454",
    }

    clickHandler = () => {
        alert("works");
    }

    render() {
        let content = this.props.content;
        return (
            <div style = {this.style} onClick={() => this.clickHandler()}>
                {this.props.caseName} 
                {this.props.assignee}
                {this.props.estimate}
            </div>
        );
    }
}

export default Card;
